import React, { Component } from 'react';
import axios from 'axios';
import { Card, Grid } from 'semantic-ui-react';

class Main extends Component {
  constructor() {
    super()
    this.state = {
      movies: []
    }
  }

  componentDidMount() {
    axios.get('http://api.tvmaze.com/search/shows?q=robot')
      .then((response) => {
        console.log('### data ###', response.data[0].show)
        const movies = response.data
        this.setState({
          movies: movies
        })
      });
  }
  render() {
    return (
    <Grid relaxed columns={4}>
      {this.state.movies.map((movie) => {
          return <Grid.Column>
                  <Card 
                    image={movie.show.image.medium}
                    header={movie.show.name}
                    meta={movie.show.genres}
            />
        </Grid.Column>})}
    </Grid>
    )
  }
}

export default Main;
