import React, { Component } from 'react';
import axios from 'axios';
import { Card } from 'semantic-ui-react';

class Main extends Component {
  constructor() {
    super()
    this.state = {
      movies: []
    }
  }

  componentDidMount() {
    axios.get('http://api.tvmaze.com/search/shows?q=robot')
      .then((response) => {
        console.log('### data ###', response.data[0].show.name)
        const movies = response.data
        this.setState({
          movies: movies
        })
      });
  }
  render() {
    return (
      <div>
        {this.state.movies.map((movie) => {return <li>{movie.show.name}</li>})}
        <Card
          image='/images/avatar/large/elliot.jpg'
          header='Elliot Baker'
          meta='Friend'
          description='Elliot is a sound engineer living in Nashville who enjoys playing guitar and hanging with his cat.'
        />
      </div>
    )
  }
}

export default Main;
